import Card from "./Card";
import {cardsData} from "../cards";
import {useState} from "react";

function Game() {

    const [cards,setCards] = useState(cardsData)

    const cardClick = (id) => {
        let tempcards = [...cards]
        const itemFinder = tempcards.findIndex(item => item.id === id)
        const isFlipped = tempcards[itemFinder].isFlipped

        tempcards[itemFinder] = {...tempcards[itemFinder] , isFlipped : !isFlipped}
        setCards([...cards])
    }

  return (
    <section className="memory-game">
        {cards.map(item => <Card key={item.id} card={item}/> )}

    </section>
  );
}

export default Game;
